/** @odoo-module **/

import { Define } from '@mail/define';

export default Define`
    {Dev/comment}
        Opens the most appropriate view that is a profile for provided id and
        model.
    {Record/insert}
        [Record/models]
            Action
        [Action/name]
            Env/openProfile
        [Action/params]
            id
                [type]
                    Integer
            model
                [type]
                    String
        [Action/behavior]
            {if}
                @model
                .{=}
                    res.partner
            .{then}
                :partner
                    {Record/insert}
                        [Record/models]
                            Partner
                        [Partner/id]
                            @id
                {Partner/openProfile}
                    @partner
                {break}
            {if}
                @model
                .{=}
                    res.users
                {break}
            .{then}
                :user
                    {User/insert}
                        [User/id]
                            @id
                {User/openProfile}
                    @user
                {break}
            {if}
                @model
                .{=}
                    mail.channel
            .{then}
                :channel
                    {Record/findById}
                        [Thread/id]
                            @id
                        [Thread/model]
                            mail.channel
                {if}
                    @channel
                    .{isFalsy}
                .{then}
                    :channel
                        {Record/doAsync}
                            [0]
                                @env
                            [1]
                                {Thread/performRpcChannelInfo}
                                    [ids]
                                        @id
                        .{Collection/first}
                {if}
                    @channel
                    .{isFalsy}
                .{then}
                    @env
                    .{Env/owlEnv}
                    .{Dict/get}
                        services
                    .{Dict/get}
                        notification
                    .{Dict/get}
                        notify
                    .{Function/call}
                        [message]
                            {Locale/text}
                                You can only open the profile of existing channels.
                        [type]
                            warning
                    {break}
                {Thread/openProfile}
                    @channel
                {break}
            {Env/openDocument}
                [id]
                    @id
                [model]
                    @model
`;
